import React, { useState } from 'react';
import axios from 'axios';
import DisplayComponent from './DisplayComponent.js';

const App = () => {
  const [numberType, setNumberType] = useState('');
  const [data, setData] = useState(null);
  const [error, setError] = useState('');

  const handleChange = (event) => {
    setNumberType(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setData(null);
    try {
      const response = await axios.get("http://localhost:9876/numbers/${numberType}");
      setData(response.data);
    } catch (err) {
      setError('Error fetching data. Please try again.');
    }
  };

  return (
    <div>
      <h1>Average Calculator</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Number Type (p, f, e, r):
          <input type="text" value={numberType} onChange={handleChange} />
        </label>
        <button type="submit">Fetch Numbers</button>
      </form>
      {error && <p>{error}</p>}
      {data && <DisplayComponent data={data} />}
    </div>
  );
};

export default App;
